function onPageLoadSetups() {
	$(document).ready(function() {
		$("div.introText").fadeIn(1e3).removeClass("hidden"), $(".scroll-to, #navigation a").click(function(e) {
			e.preventDefault();
			var t = this.href.split("#")[1];
			$("html, body").animate({
				scrollTop: $("#" + t).offset().top
			}, 1e3), $(".navbar-toggle").trigger("click")
		});
		new pureJSLightBox
	})
}
$(function() {
	onPageLoadSetups()
});